  'onSurfaceVariant',
  'outline',
  'outlineVariant',
  'shadow',
  'scrim',
  'inverseSurface',
  'inverseOnSurface',
  'inversePrimary',
];

export const TERMINAL_CSS_VARS = [
  'red',
  'redContainer',
  'onRedContainer',
  'green',
  'greenContainer',
  'onGreenContainer',
  'yellow',
  'yellowContainer',
  'onYellowContainer',
  'blue',
