import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-button',
  standalone: true,
  templateUrl: './button.component.html',
})
export class ButtonComponent {
  @Input() variant: 'primary' | 'secondary' | 'outline' = 'primary';
  @Input() size: 'small' | 'medium' | 'large' = 'medium';
  @Input() label = 'Button';
  @Input() icon?: string;
  @Output() clicked = new EventEmitter<Event>();

  get buttonClasses(): string {
    const sizeClasses = {
      small: 'btn-small',
      medium: 'btn-medium',
      large: 'btn-large',
    };

    const variantClasses = {
      primary: 'btn-primary',
      secondary: 'btn-secondary',
      outline: 'btn-outline',
    };

    return `btn-base ${sizeClasses[this.size]} ${variantClasses[this.variant]}`;
  }
}

import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-card',
  standalone: true,
  templateUrl: './card.component.html',
})
export class CardComponent {
  @Input() variant: 'default' | 'elevated' = 'default';
  @Input() padding: 'none' | 'small' | 'medium' | 'large' = 'medium';
  @Input() clickable = false;

  get cardClasses(): string {
    const classes = [`card-${this.variant}`, `card-padding-${this.padding}`];

    if (this.clickable) {
      classes.push('clickable');
    }

    return classes.join(' ');
  }
}

